tinymce.PluginManager.add('tinyzenpage', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('tinyzenpage', {
        text: 'ZP',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'tinyZenpage',
                url: url+'/tinyzenpage.php',
                width: 830,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
  
});